import React, { useState, useEffect } from 'react';
import { Box, Typography, Paper, Divider, List, ListItem, ListItemText, CircularProgress, Alert } from '@mui/material';
import api from '../../services/api';

/**
 * Компонент для отображения результатов анализа данных
 * 
 * @param {Object} props - Свойства компонента
 * @param {number} props.fileId - ID файла для анализа
 * @param {string} props.analysisType - Тип анализа (trends, competitors, metrics)
 */
const AnalyticsDisplay = ({ fileId, analysisType = 'trends' }) => {
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);
  const [analysisData, setAnalysisData] = React.useState(null);
  const [retryCount, setRetryCount] = React.useState(0);
  const [pollingActive, setPollingActive] = React.useState(false);

  // Функция для получения данных анализа
  const fetchAnalysisData = async () => {
    if (!fileId) return;

    setLoading(true);
    setError(null);

    console.log(`Запрос данных анализа: /analytics/file/${fileId}?analysis_type=${analysisType}`);

    try {
      // Получаем результаты анализа для файла
      const response = await api.get(`/analytics/file/${fileId}?analysis_type=${analysisType}`);
      console.log('Получен ответ от API:', response);

      if (response.data && response.data.length > 0) {
        // Берем последний результат анализа
        const latestAnalysis = response.data[response.data.length - 1];
        console.log('Последний результат анализа:', latestAnalysis);

        // Проверяем статус анализа
        if (latestAnalysis.status === 'pending' || latestAnalysis.status === 'processing') {
          console.log('Анализ все еще выполняется, будем опрашивать API');
          setPollingActive(true);

          // Если анализ все еще выполняется, показываем промежуточные данные
          if (latestAnalysis.result_data) {
            setAnalysisData({
              ...latestAnalysis.result_data,
              trends_analysis: latestAnalysis.result_data.trends_analysis || `Анализ данных в процессе (${latestAnalysis.status})...`
            });
          } else {
            setAnalysisData({
              trends_analysis: `Анализ данных в процессе (${latestAnalysis.status})...`,
              opportunities: [],
              threats: [],
              recommendations: []
            });
          }

          // Запускаем повторный запрос через 5 секунд
          setTimeout(() => {
            setRetryCount(prev => prev + 1);
          }, 5000);

        } else if (latestAnalysis.status === 'error') {
          // Если произошла ошибка
          console.error('Ошибка при анализе данных:', latestAnalysis.result_data?.error);
          setPollingActive(false);

          if (latestAnalysis.result_data) {
            setAnalysisData(latestAnalysis.result_data);
          } else {
            setError('Ошибка при анализе данных');
          }
        } else {
          // Если анализ завершен успешно
          console.log('Анализ завершен успешно');
          setPollingActive(false);

          // Проверяем наличие данных в result_data
          if (latestAnalysis.result_data) {
            console.log('Данные анализа:', latestAnalysis.result_data);

            // Убеждаемся, что все необходимые поля присутствуют
            const processedData = {
              trends_analysis: latestAnalysis.result_data.trends_analysis || '',
              opportunities: Array.isArray(latestAnalysis.result_data.opportunities) 
                ? latestAnalysis.result_data.opportunities 
                : [],
              threats: Array.isArray(latestAnalysis.result_data.threats) 
                ? latestAnalysis.result_data.threats 
                : [],
              recommendations: Array.isArray(latestAnalysis.result_data.recommendations) 
                ? latestAnalysis.result_data.recommendations 
                : []
            };

            setAnalysisData(processedData);
          } else {
            console.error('Данные анализа отсутствуют в ответе API');
            setError('Данные анализа отсутствуют');
          }
        }
      } else {
        console.error('Результаты анализа не найдены');
        setError('Результаты анализа не найдены');
        setPollingActive(false);
      }
    } catch (err) {
      console.error('Ошибка при получении данных анализа:', err);
      setError('Ошибка при получении данных анализа');
      setPollingActive(false);
    } finally {
      setLoading(false);
    }
  };

  // Запрашиваем данные при изменении fileId, analysisType или retryCount
  React.useEffect(() => {
    fetchAnalysisData();
  }, [fileId, analysisType, retryCount]);

  // Очищаем таймер при размонтировании компонента
  React.useEffect(() => {
    return () => {
      setPollingActive(false);
    };
  }, []);

  // Отображение загрузки
  if (loading && !analysisData) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  // Отображение ошибки
  if (error && !analysisData) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <Typography color="error">{error}</Typography>
      </Box>
    );
  }

  // Если данных нет
  if (!analysisData) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <Typography>Анализ данных недоступен</Typography>
      </Box>
    );
  }

  return (
    <Box>
      {/* Индикатор выполнения анализа */}
      {pollingActive && (
        <Alert severity="info" sx={{ mb: 2 }}>
          Анализ данных в процессе... Страница обновится автоматически.
        </Alert>
      )}

      {/* Анализ трендов */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Анализ трендов
        </Typography>
        <Divider sx={{ mb: 2 }} />
        {analysisData.trends_analysis ? (
          <Typography variant="body1" style={{ whiteSpace: 'pre-line' }}>
            {analysisData.trends_analysis}
          </Typography>
        ) : (
          <Typography variant="body2" color="textSecondary">
            Анализ трендов недоступен
          </Typography>
        )}
        {pollingActive && (
          <Box display="flex" justifyContent="center" mt={2}>
            <CircularProgress size={24} />
          </Box>
        )}
      </Paper>

      {/* Возможности */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Возможности
        </Typography>
        <Divider sx={{ mb: 2 }} />
        {analysisData.opportunities && analysisData.opportunities.length > 0 ? (
          <List>
            {analysisData.opportunities.map((item, index) => (
              <ListItem key={index}>
                <ListItemText primary={item} />
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography variant="body2" color="textSecondary">
            {pollingActive ? 'Анализ возможностей в процессе...' : 'Нет данных о возможностях'}
          </Typography>
        )}
        {pollingActive && analysisData.opportunities.length === 0 && (
          <Box display="flex" justifyContent="center" mt={2}>
            <CircularProgress size={24} />
          </Box>
        )}
      </Paper>

      {/* Угрозы */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Угрозы
        </Typography>
        <Divider sx={{ mb: 2 }} />
        {analysisData.threats && analysisData.threats.length > 0 ? (
          <List>
            {analysisData.threats.map((item, index) => (
              <ListItem key={index}>
                <ListItemText primary={item} />
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography variant="body2" color="textSecondary">
            {pollingActive ? 'Анализ угроз в процессе...' : 'Нет данных об угрозах'}
          </Typography>
        )}
        {pollingActive && analysisData.threats.length === 0 && (
          <Box display="flex" justifyContent="center" mt={2}>
            <CircularProgress size={24} />
          </Box>
        )}
      </Paper>

      {/* Рекомендации */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Рекомендации
        </Typography>
        <Divider sx={{ mb: 2 }} />
        {analysisData.recommendations && analysisData.recommendations.length > 0 ? (
          <List>
            {analysisData.recommendations.map((item, index) => (
              <ListItem key={index}>
                <ListItemText primary={item} />
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography variant="body2" color="textSecondary">
            {pollingActive ? 'Формирование рекомендаций в процессе...' : 'Нет рекомендаций'}
          </Typography>
        )}
        {pollingActive && analysisData.recommendations.length === 0 && (
          <Box display="flex" justifyContent="center" mt={2}>
            <CircularProgress size={24} />
          </Box>
        )}
      </Paper>
    </Box>
  );
};

export default AnalyticsDisplay;
