import { useSnackbar } from '../../contexts/SnackbarContext';
import api from '../../services/api';
import { useHistory } from 'react-router-dom';

const ProjectForm = ({ project = null, onSuccess }) => {
  const [name, setName] = useState(project?.name || '');
  const [description, setDescription] = useState(project?.description || '');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { showSnackbar } = useSnackbar();
  const history = useHistory();
  const isEditing = !!project;

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name.trim()) {
      setError('Название проекта обязательно');
      return;
    }

    setLoading(true);
    setError('');

    try {
      let response;

      if (isEditing) {
        response = await api.put(`/projects/${project.id}`, {
          name,
          description
        });
        showSnackbar('Проект успешно обновлен', 'success');
      } else {
        response = await api.post('/projects', {
          name,
          description
        });
        showSnackbar('Проект успешно создан', 'success');
      }

      if (onSuccess) {
        onSuccess(response.data);
      } else {
        history.push(`/projects/${response.data.id}`);
      }
    } catch (error) {
      console.error('Error saving project:', error);
      setError(
        error.response?.data?.detail || 
        'Произошла ошибка при сохранении проекта. Пожалуйста, попробуйте еще раз.'
      );
      showSnackbar('Ошибка при сохранении проекта', 'error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
      <Typography variant="h6" gutterBottom>
        {isEditing ? 'Редактирование проекта' : 'Новый проект'}
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <form onSubmit={handleSubmit}>
        <TextField
          label="Название проекта"
          value={name}
          onChange={(e) => setName(e.target.value)}
          fullWidth
          required
          margin="normal"
          disabled={loading}
        />

        <TextField
          label="Описание"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          fullWidth
          multiline
          rows={3}
          margin="normal"
          disabled={loading}
        />

        <Box sx={{ mt: 2, display: 'flex', justifyContent: 'flex-end' }}>
          <Button
            type="button"
            variant="outlined"
            onClick={() => history.goBack()}
            sx={{ mr: 1 }}
            disabled={loading}
          >
            Отмена
          </Button>

          <Button
            type="submit"
            variant="contained"
            color="primary"
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} /> : (isEditing ? 'Сохранить' : 'Создать')}
          </Button>
        </Box>
      </form>
    </Paper>
  );
};

