import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Paper, 
  Grid, 
  Button, 
  CircularProgress,
  Alert,
  Tabs,
  Tab,
  Divider
} from '@mui/material';
import AnalyticsIcon from '@mui/icons-material/Analytics';
import FileUpload from '../common/FileUpload';
import AnalyticsDisplay from '../analytics/AnalyticsDisplay';
import { useSnackbar } from '../../contexts/SnackbarContext';
import api from '../../services/api';

// Компонент для отображения списка файлов
const FilesList = ({ files, onRefresh }) => {
  const { showSnackbar } = useSnackbar();
  const [loading, setLoading] = useState(false);

  const handleDeleteFile = async (fileId) => {
    if (!window.confirm('Вы уверены, что хотите удалить этот файл?')) {
      return;
    }

    setLoading(true);
    try {
      await api.delete(`/files/files/${fileId}`);
      showSnackbar('Файл успешно удален', 'success');
      if (onRefresh) onRefresh();
    } catch (error) {
      console.error('Error deleting file:', error);
      showSnackbar('Ошибка при удалении файла', 'error');
    } finally {
      setLoading(false);
    }
  };

  if (!files || files.length === 0) {
    return (
      <Alert severity="info" sx={{ mt: 2 }}>
        Нет загруженных файлов
      </Alert>
    );
  }

  return (
    <Box sx={{ mt: 2 }}>
      <Typography variant="h6" gutterBottom>
        Загруженные файлы
      </Typography>
      <Grid container spacing={2}>
        {files.map((file) => (
          <Grid item xs={12} key={file.id}>
            <Paper sx={{ p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Box>
                <Typography variant="subtitle1">
                  {file.original_filename}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Тип: {file.file_type === 'metrics' ? 'Метрики' : 'Отчетная таблица'} | 
                  Загружен: {new Date(file.upload_date).toLocaleString()} | 
                  Статус: {file.processed ? 'Обработан' : 'В обработке'}
                </Typography>
              </Box>
              <Button 
                color="error" 
                onClick={() => handleDeleteFile(file.id)}
                disabled={loading}
              >
                Удалить
              </Button>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

const CabinetDetail = ({ match }) => {
  const cabinetId = match.params.cabinetId;
  const [cabinet, setCabinet] = useState(null);
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [tabValue, setTabValue] = useState(0);
  const [analyzing, setAnalyzing] = useState(false);
  const { showSnackbar } = useSnackbar();

  useEffect(() => {
    fetchCabinetData();
    fetchFiles();
  }, [cabinetId]);

  const fetchCabinetData = async () => {
    try {
      const response = await api.get(`/cabinets/${cabinetId}`);
      setCabinet(response.data);
    } catch (error) {
      console.error('Error fetching cabinet:', error);
      setError('Ошибка при загрузке данных кабинета');
      showSnackbar('Ошибка при загрузке данных кабинета', 'error');
    } finally {
      setLoading(false);
    }
  };

  const fetchFiles = async () => {
    try {
      const response = await api.get(`/files/files?cabinet_id=${cabinetId}`);
      setFiles(response.data);
    } catch (error) {
      console.error('Error fetching files:', error);
      showSnackbar('Ошибка при загрузке списка файлов', 'error');
    }
  };

  const handleUploadSuccess = () => {
    fetchFiles();
    showSnackbar('Файл успешно загружен и обработан', 'success');
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleAnalyzeData = async () => {
    setAnalyzing(true);
    try {
      await api.post(`/analytics/analyze`, { cabinet_id: cabinetId });
      showSnackbar('Анализ данных успешно запущен', 'success');
      setTabValue(1); // Переключаемся на вкладку с аналитикой
    } catch (error) {
      console.error('Error analyzing data:', error);
      showSnackbar('Ошибка при анализе данных', 'error');
    } finally {
      setAnalyzing(false);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert severity="error" sx={{ mb: 2 }}>
        {error}
      </Alert>
    );
  }

  if (!cabinet) {
    return (
      <Alert severity="warning" sx={{ mb: 2 }}>
        Кабинет не найден
      </Alert>
    );
  }

  return (
    <Box sx={{ mb: 4 }}>
      <Typography variant="h4" gutterBottom>
        {cabinet.name}
      </Typography>
      <Typography variant="subtitle1" color="text.secondary" gutterBottom>
        Маркетплейс: {cabinet.marketplace === 'ozon' ? 'Ozon' : 'Wildberries'}
      </Typography>

      <Divider sx={{ my: 2 }} />

      <Box sx={{ mb: 3 }}>
        <Tabs value={tabValue} onChange={handleTabChange}>
          <Tab label="Файлы" />
          <Tab label="Аналитика" />
        </Tabs>
      </Box>

      {tabValue === 0 && (
        <>
          <FileUpload cabinetId={cabinetId} onUploadSuccess={handleUploadSuccess} />
          <FilesList files={files} onRefresh={fetchFiles} />

          <Box sx={{ mt: 3, display: 'flex', justifyContent: 'center' }}>
            <Button
              variant="contained"
              color="primary"
              startIcon={<AnalyticsIcon />}
              onClick={handleAnalyzeData}
              disabled={analyzing || files.length === 0}
              sx={{ px: 4 }}
            >
              {analyzing ? <CircularProgress size={24} /> : 'Анализировать данные'}
            </Button>
          </Box>
        </>
      )}

      {tabValue === 1 && (
        <AnalyticsDisplay cabinetId={cabinetId} />
      )}
    </Box>
  );
};

export default CabinetDetail;
