import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Paper, 
  Grid, 
  CircularProgress,
  Alert,
  Card,
  CardContent,
  Divider,
  Chip,
  List,
  ListItem,
  ListItemText
} from '@mui/material';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import TrendingDownIcon from '@mui/icons-material/TrendingDown';
import TrendingFlatIcon from '@mui/icons-material/TrendingFlat';
import { useSnackbar } from '../../contexts/SnackbarContext';
import api from '../../services/api';

const AnalyticsDisplay = ({ cabinetId, reportId }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [analyticsData, setAnalyticsData] = useState(null);
  const { showSnackbar } = useSnackbar();

  useEffect(() => {
    if (reportId) {
      fetchReportAnalytics();
    } else if (cabinetId) {
      fetchCabinetAnalytics();
    }
  }, [cabinetId, reportId]);

  const fetchCabinetAnalytics = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await api.get(`/analytics/cabinet/${cabinetId}`);
      setAnalyticsData(response.data);
    } catch (error) {
      console.error('Error fetching analytics:', error);
      setError(
        error.response?.data?.detail || 
        'Произошла ошибка при загрузке аналитики. Пожалуйста, попробуйте еще раз.'
      );
      showSnackbar('Ошибка при загрузке аналитики', 'error');
    } finally {
      setLoading(false);
    }
  };

  const fetchReportAnalytics = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await api.get(`/reports/${reportId}`);
      setAnalyticsData({
        trends_analysis: response.data.content,
        opportunities: response.data.opportunities || [],
        threats: response.data.threats || [],
        recommendations: response.data.recommendations || []
      });
    } catch (error) {
      console.error('Error fetching report:', error);
      setError(
        error.response?.data?.detail || 
        'Произошла ошибка при загрузке отчета. Пожалуйста, попробуйте еще раз.'
      );
      showSnackbar('Ошибка при загрузке отчета', 'error');
    } finally {
      setLoading(false);
    }
  };

  const renderTrendIcon = (trend) => {
    if (trend === 'up') return <TrendingUpIcon color="success" />;
    if (trend === 'down') return <TrendingDownIcon color="error" />;
    return <TrendingFlatIcon color="action" />;
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert severity="error" sx={{ mb: 2 }}>
        {error}
      </Alert>
    );
  }

  if (!analyticsData) {
    return (
      <Alert severity="info" sx={{ mb: 2 }}>
        Нет данных для анализа. Пожалуйста, загрузите файлы с данными.
      </Alert>
    );
  }

  return (
    <Box sx={{ mb: 4 }}>
      <Typography variant="h5" gutterBottom>
        Аналитика данных
      </Typography>

      <Grid container spacing={3}>
        {/* Анализ трендов */}
        <Grid item xs={12}>
          <Paper elevation={3} sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Анализ трендов
            </Typography>
            <Typography variant="body1" sx={{ whiteSpace: 'pre-line' }}>
              {analyticsData.trends_analysis || 'Анализ трендов недоступен'}
            </Typography>
          </Paper>
        </Grid>

        {/* Метрики */}
        {analyticsData.metrics && (
          <Grid item xs={12}>
            <Paper elevation={3} sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom>
                Ключевые метрики
              </Typography>
              <Grid container spacing={2}>
                {Object.entries(analyticsData.metrics).map(([key, value]) => (
                  <Grid item xs={12} sm={6} md={4} key={key}>
                    <Card>
                      <CardContent>
                        <Typography variant="subtitle1" color="text.secondary">
                          {key}
                        </Typography>
                        <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                          <Typography variant="h5" component="div">
                            {typeof value === 'object' ? value.value : value}
                          </Typography>
                          {typeof value === 'object' && value.trend && (
                            <Box sx={{ ml: 1 }}>
                              {renderTrendIcon(value.trend)}
                            </Box>
                          )}
                        </Box>
                        {typeof value === 'object' && value.change && (
                          <Typography variant="body2" color={value.change > 0 ? 'success.main' : 'error.main'}>
                            {value.change > 0 ? '+' : ''}{value.change}%
                          </Typography>
                        )}
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Paper>
          </Grid>
        )}

        {/* Возможности и угрозы */}
        <Grid item xs={12} md={6}>
          <Paper elevation={3} sx={{ p: 3, height: '100%' }}>
            <Typography variant="h6" gutterBottom>
              Возможности
            </Typography>
            <List>
              {analyticsData.opportunities && analyticsData.opportunities.length > 0 ? (
                analyticsData.opportunities.map((item, index) => (
                  <ListItem key={index} divider={index < analyticsData.opportunities.length - 1}>
                    <ListItemText primary={item} />
                  </ListItem>
                ))
              ) : (
                <ListItem>
                  <ListItemText primary="Нет данных о возможностях" />
                </ListItem>
              )}
            </List>
          </Paper>
        </Grid>

        <Grid item xs={12} md={6}>
          <Paper elevation={3} sx={{ p: 3, height: '100%' }}>
            <Typography variant="h6" gutterBottom>
              Угрозы
            </Typography>
            <List>
              {analyticsData.threats && analyticsData.threats.length > 0 ? (
                analyticsData.threats.map((item, index) => (
                  <ListItem key={index} divider={index < analyticsData.threats.length - 1}>
                    <ListItemText primary={item} />
                  </ListItem>
                ))
              ) : (
                <ListItem>
                  <ListItemText primary="Нет данных об угрозах" />
                </ListItem>
              )}
            </List>
          </Paper>
        </Grid>

        {/* Рекомендации */}
        <Grid item xs={12}>
          <Paper elevation={3} sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Рекомендации
            </Typography>
            <List>
              {analyticsData.recommendations && analyticsData.recommendations.length > 0 ? (
                analyticsData.recommendations.map((item, index) => (
                  <ListItem key={index} divider={index < analyticsData.recommendations.length - 1}>
                    <ListItemText 
                      primary={item.title || item}
                      secondary={item.description}
                    />
                  </ListItem>
                ))
              ) : (
                <ListItem>
                  <ListItemText primary="Нет рекомендаций" />
                </ListItem>
              )}
            </List>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default AnalyticsDisplay;
