import React, { useState, useEffect } from 'react';
import { Box, Typography, Paper, Divider, List, ListItem, ListItemText, CircularProgress } from '@mui/material';
import api from '../../services/api';

/**
 * Компонент для отображения результатов анализа данных
 * 
 * @param {Object} props - Свойства компонента
 * @param {number} props.fileId - ID файла для анализа
 * @param {string} props.analysisType - Тип анализа (trends, competitors, metrics)
 */
const AnalyticsDisplay = ({ fileId, analysisType = 'trends' }) => {
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);
  const [analysisData, setAnalysisData] = React.useState(null);

  React.useEffect(() => {
    const fetchAnalysisData = async () => {
      if (!fileId) return;

      setLoading(true);
      setError(null);

      console.log(`Запрос данных анализа: /analytics/file/${fileId}?analysis_type=${analysisType}`);

      try {
        // Получаем результаты анализа для файла
        const response = await api.get(`/analytics/file/${fileId}?analysis_type=${analysisType}`);
        console.log('Получен ответ от API:', response);

        if (response.data && response.data.length > 0) {
          // Берем последний результат анализа
          const latestAnalysis = response.data[response.data.length - 1];
          console.log('Последний результат анализа:', latestAnalysis);

          // Проверяем наличие данных в result_data
          if (latestAnalysis.result_data) {
            console.log('Данные анализа:', latestAnalysis.result_data);
            setAnalysisData(latestAnalysis.result_data);
          } else {
            console.error('Данные анализа отсутствуют в ответе API');
            setError('Данные анализа отсутствуют');
          }
        } else {
          console.error('Результаты анализа не найдены');
          setError('Результаты анализа не найдены');
        }
      } catch (err) {
        console.error('Ошибка при получении данных анализа:', err);
        setError('Ошибка при получении данных анализа');
      } finally {
        setLoading(false);
      }
    };

    fetchAnalysisData();
  }, [fileId, analysisType]);

  // Отображение загрузки
  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  // Отображение ошибки
  if (error) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <Typography color="error">{error}</Typography>
      </Box>
    );
  }

  // Если данных нет
  if (!analysisData) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <Typography>Анализ данных недоступен</Typography>
      </Box>
    );
  }

  return (
    <Box>
      {/* Анализ трендов */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Анализ трендов
        </Typography>
        <Divider sx={{ mb: 2 }} />
        {analysisData.trends_analysis ? (
          <Typography variant="body1" style={{ whiteSpace: 'pre-line' }}>
            {analysisData.trends_analysis}
          </Typography>
        ) : (
          <Typography variant="body2" color="textSecondary">
            Анализ трендов недоступен
          </Typography>
        )}
      </Paper>

      {/* Возможности */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Возможности
        </Typography>
        <Divider sx={{ mb: 2 }} />
        {analysisData.opportunities && analysisData.opportunities.length > 0 ? (
          <List>
            {analysisData.opportunities.map((item, index) => (
              <ListItem key={index}>
                <ListItemText primary={item} />
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography variant="body2" color="textSecondary">
            Нет данных о возможностях
          </Typography>
        )}
      </Paper>

      {/* Угрозы */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Угрозы
        </Typography>
        <Divider sx={{ mb: 2 }} />
        {analysisData.threats && analysisData.threats.length > 0 ? (
          <List>
            {analysisData.threats.map((item, index) => (
              <ListItem key={index}>
                <ListItemText primary={item} />
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography variant="body2" color="textSecondary">
            Нет данных об угрозах
          </Typography>
        )}
      </Paper>

      {/* Рекомендации */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Рекомендации
        </Typography>
        <Divider sx={{ mb: 2 }} />
        {analysisData.recommendations && analysisData.recommendations.length > 0 ? (
          <List>
            {analysisData.recommendations.map((item, index) => (
              <ListItem key={index}>
                <ListItemText primary={item} />
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography variant="body2" color="textSecondary">
            Нет рекомендаций
          </Typography>
        )}
      </Paper>
    </Box>
  );
};

export default AnalyticsDisplay;
